protected void appendIdentityHashCode(StringBuffer buffer, Object object) {
    if (this.isUseIdentityHashCode() && object!=null) {
        register(object);
        buffer.append('@');
        buffer.append(Integer.toHexString(System.identityHashCode(object)));
    }
}

