    CharSet chars = CharSet.getInstance(set);
    int count = 0;
    char[] chrs = str.toCharArray();
    int sz = chrs.length;
    for(int i=0; i<sz; i++) {
        if(chars.contains(chrs[i])) {
            count++;
        }
    }
    return count;
}

