    while (cls != null) {
        Class<?>[] interfaces = cls.getInterfaces();

        for (Class<?> i : interfaces) {
            if (interfacesFound.add(i)) {
                getAllInterfaces(i, interfacesFound);
            }
        }

        cls = cls.getSuperclass();
     }
 }

