    if (JAVA_VERSION != null) {
        for (int i = 0; i < JAVA_VERSION.length(); i++) {
            char ch = JAVA_VERSION.charAt(i);
            if (ch >= '0' && ch <= '9') {
                return JAVA_VERSION.substring(i);
