private Format getFormat(String desc) {
    if (registry != null) {
        String name = desc;
        String args = null;
        int i = desc.indexOf(START_FMT);
        if (i > 0) {
            name = desc.substring(0, i).trim();
            args = desc.substring(i + 1).trim();
        }
        FormatFactory factory = registry.get(name);
        if (factory != null) {
            return factory.getFormat(name, args, getLocale());
        }
    }
    return null;
}

private int readArgumentIndex(String pattern, ParsePosition pos) {
    int start = pos.getIndex();
    seekNonWs(pattern, pos);
    StringBuffer result = new StringBuffer();
    boolean error = false;
    for (; !error && pos.getIndex() < pattern.length(); next(pos)) {
        char c = pattern.charAt(pos.getIndex());
        if (Character.isWhitespace(c)) {
            seekNonWs(pattern, pos);
            c = pattern.charAt(pos.getIndex());
            if (c != START_FMT && c != END_FE) {
                error = true;
                continue;
            }
        }
        if ((c == START_FMT || c == END_FE) && result.length() > 0) {
            try {
                return Integer.parseInt(result.toString());
            } catch (NumberFormatException e) {
                // we've already ensured only digits, so unless something
                // outlandishly large was specified we should be okay.
            }
        }
        error = !Character.isDigit(c);
        result.append(c);
    }
    if (error) {
        throw new IllegalArgumentException(
                "Invalid format argument index at position " + start + ": "
                        + pattern.substring(start, pos.getIndex()));
    }
    throw new IllegalArgumentException(
            "Unterminated format element at position " + start);
}

private String parseFormatDescription(String pattern, ParsePosition pos) {
    int start = pos.getIndex();
    seekNonWs(pattern, pos);
    int text = pos.getIndex();
    int depth = 1;
    for (; pos.getIndex() < pattern.length(); next(pos)) {
        switch (pattern.charAt(pos.getIndex())) {
        case START_FE:
            depth++;
            break;
        case END_FE:
            depth--;
            if (depth == 0) {
                return pattern.substring(text, pos.getIndex());
            }
            break;
        case QUOTE:
            getQuotedString(pattern, pos, false);
            break;
        }
    }
    throw new IllegalArgumentException(
            "Unterminated format element at position " + start);
}

private String insertFormats(String pattern, ArrayList<String> customPatterns) {
    if (!containsElements(customPatterns)) {
        return pattern;
    }
    StringBuilder sb = new StringBuilder(pattern.length() * 2);
    ParsePosition pos = new ParsePosition(0);
    int fe = -1;
    int depth = 0;
    while (pos.getIndex() < pattern.length()) {
        char c = pattern.charAt(pos.getIndex());
        switch (c) {
        case QUOTE:
            appendQuotedString(pattern, pos, sb, false);
            break;
        case START_FE:
            depth++;
            if (depth == 1) {
                fe++;
                sb.append(START_FE).append(
                        readArgumentIndex(pattern, next(pos)));
                String customPattern = customPatterns.get(fe);
                if (customPattern != null) {
                    sb.append(START_FMT).append(customPattern);
                }
            }
            break;
        case END_FE:
            depth--;
            //$FALL-THROUGH$
        default:
            sb.append(c);
            next(pos);
        }
    }
    return sb.toString();
}

private void seekNonWs(String pattern, ParsePosition pos) {
    int len = 0;
    char[] buffer = pattern.toCharArray();
    do {
        len = StrMatcher.splitMatcher().isMatch(buffer, pos.getIndex());
        pos.setIndex(pos.getIndex() + len);
    } while (len > 0 && pos.getIndex() < pattern.length());
}

private ParsePosition next(ParsePosition pos) {
    pos.setIndex(pos.getIndex() + 1);
    return pos;
}

private StringBuilder appendQuotedString(String pattern, ParsePosition pos,
        StringBuilder appendTo, boolean escapingOn) {
    int start = pos.getIndex();
    char[] c = pattern.toCharArray();
    if (escapingOn && c[start] == QUOTE) {
        next(pos);
        return appendTo == null ? null : appendTo.append(QUOTE);
    }
    int lastHold = start;
    for (int i = pos.getIndex(); i < pattern.length(); i++) {
        if (escapingOn && pattern.substring(i).startsWith(ESCAPED_QUOTE)) {
            appendTo.append(c, lastHold, pos.getIndex() - lastHold).append(
                    QUOTE);
            pos.setIndex(i + ESCAPED_QUOTE.length());
            lastHold = pos.getIndex();
            continue;
        }
        switch (c[pos.getIndex()]) {
        case QUOTE:
            next(pos);
            return appendTo == null ? null : appendTo.append(c, lastHold,
                    pos.getIndex() - lastHold);
        default:
            next(pos);
        }
    }
    throw new IllegalArgumentException(
            "Unterminated quoted string at position " + start);
}

private void getQuotedString(String pattern, ParsePosition pos,
        boolean escapingOn) {
    appendQuotedString(pattern, pos, null, escapingOn);
}

private boolean containsElements(Collection<?> coll) {
    if (coll == null || coll.size() == 0) {
        return false;
    }
    for (Iterator<?> iter = coll.iterator(); iter.hasNext();) {
        if (iter.next() != null) {
            return true;
        }
    }
    return false;
}

