        ScheduledThreadPoolExecutor s = new ScheduledThreadPoolExecutor(
                THREAD_POOL_SIZE);
        s.setContinueExistingPeriodicTasksAfterShutdownPolicy(false);
        s.setExecuteExistingDelayedTasksAfterShutdownPolicy(false);
        executorService = s;
        ownExecutor = true;
    }

    setLimit(limit);
}

public final synchronized int getLimit() {
    return limit;
}

public final synchronized void setLimit(int limit) {
    this.limit = limit;
}

public synchronized void shutdown() {
    if (!shutdown) {

        if (ownExecutor) {
            // if the executor was created by this instance, it has
            // to be shutdown
            getExecutorService().shutdownNow();
        }
        if (task != null) {
            task.cancel(false);
        }

        shutdown = true;
    }
}

public synchronized boolean isShutdown() {
    return shutdown;
}

public synchronized void acquire() throws InterruptedException {
    if (isShutdown()) {
        throw new IllegalStateException("TimedSemaphore is shut down!");
    }

    if (task == null) {
        task = startTimer();
    }

    boolean canPass = false;
    do {
        canPass = getLimit() <= NO_LIMIT || acquireCount < getLimit();
        if (!canPass) {
            wait();
        } else {
            acquireCount++;
        }
    } while (!canPass);
}

